import * as THREE from "three";
import gsap from "gsap";
import vertex from "@/shader/flyLine/vertex.glsl";
import fragment from "@/shader/flyLine/fragment.glsl";

export default class FlyLineShader {

  constructor(){
    let FlyLinePoints=[
      new THREE.Vector3(0,0,0),
      new THREE.Vector3(-5,4,0),
      new THREE.Vector3(-10,0,0),
    ];
    //创建曲线
    this.lineCurve=new THREE.CatmullRomCurve3(FlyLinePoints);
    const points = this.lineCurve.getPoints(5000);
    //创建集合顶点
    this.geometry=new THREE.BufferGeometry().setFromPoints(points);
    //给每一个顶点设置属性
    const attributes = {
      size: {
        value: new Float32Array(points.length)
      },
      color: {
        value: new Float32Array(points.length * 3)
      }
    };

    //设置着色器材质
    this.shaderMaterial=new THREE.ShaderMaterial({
      vertexShader:vertex,
      fragmentShader:fragment,
      uniforms:{
        uTime:{
          value:0
        },
        uColor:{
          value:new THREE.Color(0xffff00)
        },
        uSize:{
          value:0.01
        }
      }
    });

    this.mesh=new THREE.Points(this.geometry,this.shaderMaterial);
    gsap.to(this.shaderMaterial.uniforms.uTime,{
      value:1000,
      duration:1,
      repeat:-1,
      ease:"none"
    });
  }
  
}
