import * as THREE from "three"; 
export default class AlarmSprite{
  constructor(){
    const TextureLoader = new THREE.TextureLoader();
    const map = TextureLoader.load( "./textures/tag/fire.png" );
    this.material = new THREE.SpriteMaterial( { map: map } );

    this.mesh = new THREE.Sprite( this.material );
   
    this.mesh.position.set(-4.2,2.5,4);

    //封装点击事件，经过投射光线检测获取点击对象
    this.fns=[];
    //创建射线
    this.raycaster = new THREE.Raycaster();
    this.mouse = new THREE.Vector2();
    this.raycaster.setFromCamera(this.mouse, camera);
    event.mesh = this.mesh;
    event.alarm=this;
    window.addEventListener('click',(event)=>{
      this.mouse.x = ( event.clientX / window.innerWidth ) * 2 - 1;
      this.mouse.y = - ( event.clientY / window.innerHeight ) * 2 + 1;
      this.fns.forEach((fn)=>{
        fn(event);
      })
})
  }
  onClick(fn){
    this.fns.push(fn);

  }
}
